<?php defined('SYSPATH') or die('No direct script access.');/** * formerror helper class. */class Helper_Color {	public static function hex2RGB($hex) {		$hex = preg_replace("/#/", "", $hex);		$color = array(); 		if(strlen($hex) == 3) {			$color['r'] = hexdec(substr($hex, 0, 1) . $r);			$color['g'] = hexdec(substr($hex, 1, 1) . $g);			$color['b'] = hexdec(substr($hex, 2, 1) . $b);		}		else if(strlen($hex) == 6) {			$color['r'] = hexdec(substr($hex, 0, 2));			$color['g'] = hexdec(substr($hex, 2, 2));			$color['b'] = hexdec(substr($hex, 4, 2));		} 		return $color;	} 	public static function RGB2hex($r, $g, $b) {		//String padding bug found and the solution put forth by Pete Williams (http://snipplr.com/users/PeteW)		$hex = "#";		$hex.= str_pad(dechex($r), 2, "0", STR_PAD_LEFT);		$hex.= str_pad(dechex($g), 2, "0", STR_PAD_LEFT);		$hex.= str_pad(dechex($b), 2, "0", STR_PAD_LEFT); 		return $hex;	}}