<?php defined('SYSPATH') or die('No direct script access.');/** * formerror helper class. */class Helper_Error {	public static function form($str,$linebreak=FALSE)	{			if (!empty($str))		{			$error = '<div class="error">'.$str.'</div>';						return $error;		}	}		public static function text($str,$linebreak=FALSE)	{			if (!empty($str))		{			$error = '<span style="color:red">'.$str.'</span>';						return $error;		}	}}