<?php defined('SYSPATH') or die('No direct script access.');/** * formerror helper class. */class Helper_Match {	public static function date($date = NULL)	{			if (preg_match('/^(\d{1,2})[^\d]*?(\d{1,2})[^\d]*?(\d{2,4})?$/ui',$date,$match))		{			$day = $match[1];			$month = $match[2];						if (isset($match[3]))			{				$year = $match[3] > 100 ? $match[3] : 2000 + $match[3];			}			else			{				$year = date('Y');			}						return mktime(0,0,0,$month,$day,$year);		}						return NULL;	}}