<?php defined('SYSPATH') or die('No direct script access.');/** * formerror helper class. */class Helper_Time {	public static function date2timestamp($date)	{			//$time = time::date_parse_from_format('dd/mm/yyyy',$date);				if ($time = time::date_parse($date))		{			return mktime(0,0,0,$time['month'],$time['day'],$time['year']);		}				return mktime();	}		public static function date_parse($date)	{		$time = array();		if (preg_match('/([0-9]{1,2})\/([0-9]{1,2})\/([0-9]{4})/',$date,$match))		{			$time['day'] = $match[1];			$time['month'] = $match[2];			$time['year'] = $match[3];			return $time;		}				return false;	}}