<?php defined('SYSPATH') or die('No direct script access.');class Model_Flow_User extends ORM { 	 	// Relationships	protected $_belongs_to = array(		'user' => array(),		'flow' 	  => array()	);		protected $_table_name = 'flows_users';			public function save()	{		if (!parent::__isset('created_timestamp'))		{			parent::__set('created_timestamp',time());		}				parent::save();	}}