<?php defined('SYSPATH') or die('No direct script access.');class Model_Stream extends ORM {  	protected $_has_many = array(		'events' 	=> array('model' => 'event'),		'editors'   => array('model' => 'user', 'through' => 'editors_streams'),		'invitations' => array('model' => 'invitation')	);		protected $_belongs_to = array(		'flow'		=> array('model'=>'flow')	);		protected $_rules = array(		'flow_id' => array(			'not_empty' 	=> NULL,			'regex' 		=> array('/[0-9]*/')		),		'uri' => array(		    'not_empty'		=> NULL,			'min_length' 	=> array(2),			'max_length' 	=> array(255),			'regex' 		=> array('/^[-\pL\pN_.]++$/uD')		),		'color' => array(			'not_empty'  => NULL,			'min_length' => array(7),			'max_length' => array(7),			'regex' => array('/#[0-9a-fA-F]{6}/')		),		'title' => array(			'not_empty' => NULL,			'min_length' => array(4),			'max_length' => array(255)		),		'description' => array(			'not_empty' => NULL		),		'created_timestamp' => array(			'not_empty' => NULL		)	);  	// Validation callbacks	protected $_callbacks = array(		'uri' => array('uri_available')	);  	// Field labels	protected $_labels = array(		'uri'         => 'uri',		'color'       => 'color',		'title'       => 'title',		'description' => 'description'	); 		public function validate_create(& $array) 	{		// Initialise the validation library and setup some rules				$array = Validate::factory($array)						->rules('flow_id',$this->_rules['flow_id'])						->rules('uri',$this->_rules['uri'])						->rules('color', $this->_rules['color'])						->rules('title', $this->_rules['title'])						->rules('description', $this->_rules['description'])						->filter('uri','trim')						->filter('color', 'trim')						->filter('title', 'trim')						->filter('description', 'trim');  		#Executes username callbacks defined in parent				foreach($this->_callbacks['uri'] as $callback){			$array->callback('uri', array($this, $callback));		} 		return $array;	}		public function validate_edit(& $array)	{			// Initialise the validation library and setup some rules				$array = Validate::factory($array)						->rules('uri',$this->_rules['uri'])						->rules('color', $this->_rules['color'])						->rules('title', $this->_rules['title'])						->rules('description', $this->_rules['description'])						->filter('uri','trim')						->filter('color', 'trim')						->filter('title', 'trim')						->filter('description', 'trim');				#Executes username callbacks defined in parent				foreach($this->_callbacks['uri'] as $callback){			$array->callback('uri', array($this, $callback));		}			return $array;	}		/**	 * Does the reverse of unique_key_exists() by triggering error if email exists.	 * Validation callback.	 *	 * @param   Validate  Validate object	 * @param   string    field name	 * @return  void	 */	public function uri_available(Validate $array, $field)	{		// Load the configuration for this type		$blacklist = Kohana::config('routing')->get('blacklist');				if (in_array($array[$field],$blacklist))		{			$array->error($field, 'uri_available', array($array[$field]));		}		elseif ($this->unique_key_exists($array[$field], 'uri'))		{			$array->error($field, 'uri_available', array($array[$field]));		}	}		/**	 * Tests if a unique key value exists in the database.	 *	 * @param   mixed    the value to test	 * @param   string   field name	 * @return  boolean	 */	public function unique_key_exists($value, $field)	{		return (bool) DB::select(array('COUNT("*")', 'total_count'))			->from($this->_table_name)			->where($field, '=', $value)			->where($this->_primary_key, '!=', $this->pk())			->execute($this->_db)			->get('total_count');	}		public function unique_key($value)	{		return Validate::numeric($value) ? 'id' : 'uri';	}		public function save()	{		if (!parent::__isset('created_timestamp')) {			parent::__set('created_timestamp',time());		}				parent::save();	}	}