<?php echo form::open(NULL, array('id' => 'login')) ?><h1><?php echo __('Register') ?></h1><h4><?php echo form::label('username',__('Username')) ?></h4><? if (isset($errors['username'])) echo $errors['username']; ?><?php echo form::input('username',isset($post) ? $post['username'] : '')?><h4><?php echo form::label('email',__('Email')) ?></h4><? if (isset($errors['email'])) echo $errors['email']; ?><?php echo form::input('email',isset($post) ? $post['email'] : '') ?><h4><?php echo form::label('password',__('Password')) ?></h4><? if (isset($errors['password'])) echo $errors['password']; ?><?php echo form::password('password') ?><h4><?php echo form::label('password_confirm',__('Confirm Password')) ?></h4><? if (isset($errors['password_confirm'])) echo $errors['password_confirm']; ?><?php echo form::password('password_confirm') ?><br/><?php echo form::button(NULL, __('Register'), array('type' => 'submit')) ?><?php echo form::close() ?>