<div id="profile_picture">	<img src="<?= url::site($user->picture) ?>" /></div><div id="profile_info"><p id="user_name">@<?= ucfirst($user->username)?></p><p id="full_name">	<?= $user->fullname!='' ? $user->fullname : error::text(__('No full name')) ?></p><p><?= $user->email ?></p><div id="user_description">	<?= $user->description!='' ? TEXT::auto_p($user->description) :  error::text(__('No description')) ?></div><p><?= $user->logins.' '.__('total logins').'. '.__('Last login').' '.date('d/m - Y',$user->last_login) ?>.</p></div>