<div id="profile_picture">	<img src="<?= url::site($user->picture) ?>" /></div><div id="profile_info"><h1 id="user_name"><?= ucfirst(html::entities($user->username)) ?></h1><p id="full_name">	<?= $user->fullname!='' ? html::entities($user->fullname) : error::text(__('No full name')) ?></p><p><?= $user->email ?></p><div id="user_description">	<?= $user->description!='' ? Text::auto_p($user->description) :  error::text(__('No description')) ?></div><p><?= $user->logins.' '.__('total logins').'. '.__('Last login').' '.date('d/m - Y',$user->last_login) ?>.</p></div>