<?php echo form::open(NULL, array('id' => 'create_event')) ?><h1><?php echo __('New event') ?></h1><h4><?php echo form::label('title',__('Title')) ?></h4><? if (isset($errors['title'])) echo $errors['title']; ?><? if (isset($errors['uri'])) echo $errors['uri']; ?><?php echo form::input('title',isset($post) ? $post['title'] : '') ?><h4><?php echo form::label('tags',__('Tags')) ?></h4><ul><?php foreach($flow_tags as $tag): ?>	<li><?php echo form::checkbox('tags['.$tag->id.']',TRUE,isset($post['tags'][$tag->id]) ? TRUE : isset($event_tags[$tag->id])) ?> <?php echo $tag->title; ?></li><?php endforeach; ?></ul><h4><?php echo form::label('description',__('Description')) ?></h4><? if (isset($errors['description'])) echo $errors['description']; ?><?php echo form::textarea('description',isset($post) ? $post['description'] : '') ?><h4><?php echo form::label('notes',__('Private notes')) ?></h4><? if (isset($errors['notes'])) echo $errors['notes']; ?><?php echo form::textarea('notes',isset($post) ? $post['notes'] : '') ?><h4><?php echo form::label('time',__('Time')) ?></h4><? if (isset($errors['date'])) echo $errors['date']; ?><?php echo form::input('date',isset($post) ? date('d/m/Y',$post['timestamp']) : '',array('id'=>'date','class'=>'datepicker')) ?><?php echo form::select('hours',array_combine(range(00,23,1),range(00,23,1)),isset($post) ? date('H', $post['timestamp']) : 0) ?> :<?php echo form::select('minutes',array_combine(range(0,45,15),range(0,45,15)),isset($post) ? date('i',$post['timestamp']) : 0) ?><h4><?php echo form::label('duration',__('Duration')) ?></h4><? if (isset($errors['duration'])) echo $errors['duration']; ?><?php echo form::select('duration',$durations,isset($post) ? $post['duration'] : '') ?><br/><br/><?php echo form::button(NULL, __('Create'), array('type' => 'submit')) ?><?php echo form::close() ?><script type="text/javascript">$(function(){	$('.datepicker').datePicker();		var start_date = '<?php echo $flow->start_date ?>';	var end_date = '<?php echo $flow->end_date ?>';		$('#date').dpSetStartDate(start_date);	$('#date').dpSetEndDate(end_date);});</script>