<?php echo html::anchor($flow->uri.'/delete',__('Delete flow'),array('class'=>'action')) ?><?php echo form::open(NULL, array('id' => 'edit_flow')) ?><h4><?php echo form::label('title',__('Title')) ?></h4><? if (isset($errors['title'])) echo error::form($errors['title']); ?><?php echo form::input('title',isset($post) ? $post['title'] : $flow->title) ?><h4><?php echo form::label('uri',__('URI')) ?></h4><? if (isset($errors['uri'])) echo error::form($errors['uri']); ?><?php echo form::input('uri',isset($post) ? $post['uri'] : $flow->uri)?><h4><?php echo form::label('description',__('Description')) ?></h4><? if (isset($errors['description'])) echo error::form($errors['description']); ?><?php echo form::textarea('description',isset($post) ? $post['description'] : $flow->description) ?><div class="clearfix"><h4><?php echo form::label('start_date',__('Start date')) ?></h4><? if (isset($errors['start_date'])) echo error::form($errors['start_date']); ?><?php echo form::input('start_date',isset($post) ? $post['start_date'] : $flow->start_date,array('id'=>'start_date','class'=>'datepicker')) ?></div><div class="clearfix"><h4><?php echo form::label('end_date',__('End date')) ?></h4><? if (isset($errors['end_date'])) echo error::form($errors['end_date']); ?><?php echo form::input('end_date',isset($post) ? $post['end_date'] :$flow->end_date,array('id'=>'end_date','class'=>'datepicker')) ?><br/><br/><br/><?php echo form::button(NULL, __('Save'), array('type' => 'submit')) ?></div><?php echo form::close() ?><script type="text/javascript">$(function(){	$('#start_date').datePicker(		{			endDate: '<?php echo isset($post) ? $post['end_date'] : $flow->end_date ?>',		}	);		$('#end_date').datePicker(		{			startDate: '<?php echo isset($post) ? $post['start_date'] : $flow->start_date ?>',		}	);	$('#start_date').bind(		'dpClosed',		function(e, selectedDates)		{			var d = selectedDates[0];			if (d) {				d = new Date(d);				$('#end_date').dpSetStartDate(d.addDays(1).asString());			}		}	);	$('#end_date').bind(		'dpClosed',		function(e, selectedDates)		{			var d = selectedDates[0];			if (d) {				d = new Date(d);				$('#start_date').dpSetEndDate(d.addDays(-1).asString());			}		}	);});</script>