<?php echo html::anchor($flow->uri.'/edit',__('Edit flow')) ?><?php echo html::anchor($flow->uri.'/delete',__('Delete flow')) ?><div class="flow_logo">	<img src="<?php echo $flow->logo ?>" /></div><h2><?php echo $flow->title ?></h2><div class="">	<?php echo $flow->description ?></div><h3>Streams</h3><ul>	<?php foreach($streams as $stream): ?>		<li><?php echo $stream->title ?></li>	<?php endforeach; ?></ul><h3>Tags</h3><?php echo html::anchor($flow->uri.'/tags',__('Manage tags')) ?><ul>	<?php foreach($tags as $tag): ?>		<li><?php echo $tag->title ?></li>	<? endforeach; ?></ul><h3><?php echo __('Flow users') ?></h3><?php echo html::anchor($flow->uri.'/invitations',__('Invite users')) ?><ul><?php foreach($flow_users as $flow_user): ?>	<li>		<?php echo html::anchor($flow_user->user->uri,$flow_user->user->name) ?> 		<?php echo $flow_user->master==1 ? '(master)' : '' ?>	</li><?php endforeach; ?></ul>