<?php if ($access['master']): ?>	<?php echo html::anchor($flow->uri.'/edit',__('Edit flow')) ?>	<?php echo html::anchor($flow->uri.'/delete',__('Delete flow')) ?><?php endif; ?><!--<div class="flow_logo">	<img src="<?php echo $flow->logo ?>" /></div>--><div class="">	<?php echo $flow->description ?></div><h3>Streams</h3><?php if ($access['master']): ?><?php echo html::anchor(Route::get('stream-new')->uri(array('flow'=>$flow->uri)),__('Add stream')) ?><?php endif; ?><div class="overview"><div class="streams_info"><div class="clearfix">&nbsp;</div><div class="clearfix">&nbsp;</div><ul>	<?php foreach($streams as $stream): ?>		<li style="background-color: <?php echo $stream->color ?>">			<div>				<?php echo html::anchor($flow->uri.'/'.$stream->uri,$stream->title) ?>			</div>		</li>	<?php endforeach; ?></ul></div><div class="streams"><ul class="timeline clearfix" style="width: <?php echo ceil((time::date2timestamp($flow->end_date)-time::date2timestamp($flow->start_date))/(30*60))*144 ?>px">	<?php $time = time::date2timestamp($flow->start_date)+1; ?>	<?php while ($time < time::date2timestamp($flow->end_date)): ?>		<li class="day"><?php echo date('l j \o\f F Y',$time) ?></li>		<?php $time+= 24*3600; ?>	<? endwhile; ?></ul><ul class="timeline clearfix" style="width: <?php echo ceil((time::date2timestamp($flow->end_date)-time::date2timestamp($flow->start_date))/(30*60))*144 ?>px">	<li class="halfhour">&nbsp;</li>	<?php $time = 1; ?>	<?php while ($time*3600 < time::date2timestamp($flow->end_date) - time::date2timestamp($flow->start_date)): ?>		<li class="hour"><?php echo $time%24 ?></li>		<?php $time++; ?>	<? endwhile; ?></ul><?php foreach ($streams as $stream): ?><?php $c = color::hex2RGB($stream->color); ?><ul class="stream" style="width: <?php echo ceil((time::date2timestamp($flow->end_date)-time::date2timestamp($flow->start_date))/(30*60))*144 ?>px; background-color: rgba(<?php echo $c['r'].','.$c['g'].','.$c['b'].',0.3' ?>)"><?php foreach($stream->events->find_all() as $event): ?>	<li style="left: <?php echo ceil(($event->timestamp-time::date2timestamp($flow->start_date))/(60*15))*72 ?>px; width: <?php echo ceil($event->duration/30)*144 ?>px">		<div style="background-color: <?php echo $stream->color ?>">			<?php echo html::anchor(Route::get('event')->uri(array('flow'=>$flow->uri,'stream'=>$stream->uri,'event'=>$event->id)),$event->title) ?>			<?php foreach($event->tags->find_all() as $tag): ?>				<a href="<?php echo url::site($flow->uri.'/tags/'.$tag->id) ?>" class="tooltip marker tag<?php echo $tag->id ?>" title="<?php echo $tag->title ?>"></a>			<?php endforeach; ?>		</div>	</li><?php endforeach; ?></ul><?php endforeach; ?></div></div><h3>Tags</h3><?php if ($access['master']): ?><?php echo html::anchor($flow->uri.'/tags',__('Manage tags')) ?><?php endif; ?><ul class="tags">	<?php foreach($tags as $tag): ?>		<li><?php echo $tag->render('html') ?></li>	<? endforeach; ?></ul><h3><?php echo __('Flow users') ?></h3><?php if ($access['master']): ?><?php echo html::anchor($flow->uri.'/invitations',__('Invite users')) ?><?php endif; ?><ul><?php foreach($flow_users as $flow_user): ?>	<li>		<?php echo html::anchor($flow_user->user->uri,$flow_user->user->name) ?> 		<?php echo $flow_user->master==1 ? '(master)' : '' ?>	</li><?php endforeach; ?></ul><style><?php foreach($tags as $tag): ?>.tag<?php echo $tag->id ?> {	background-color: <?php echo $tag->color ?>}<?php endforeach; ?></style><script type="text/javascript">$(function(){ 	$('a.tooltip').tipsy({live: true});});</script>