<!DOCTYPE html PUBLIC "-//W3C//DTD XHTML 1.0 Transitional//EN""http://www.w3.org/TR/xhtml1/DTD/xhtml1-transitional.dtd"><html xmlns="http://www.w3.org/1999/xhtml" xml:lang="en" lang="en">  <head>  <meta name="language" content="dan">  <meta name="date" content="<?= date('m-d-Y') ?>">  <meta name="robots" content="index, follow">  <meta name="googlebot" content="noarchive">   <meta http-equiv="Content-type" content="text/html; charset=UTF-8" />  <meta http-equiv="Content-Language" content="en-us" />  <title><?php echo $title;?></title>  <meta name="title" content="<?php echo $title;?>">  <meta name="keywords" content="<?php echo $meta_keywords;?>" />  <meta name="description" content="<?php echo $meta_description;?>" />  <meta name="copyright" content="<?php echo $meta_copywrite;?>" />  <base href="<?= url::site() ?>">   <link rel="image_src" href="<?= url::site($image_src) ?>" />    <?php foreach($styles['common'] as $file => $type) { echo HTML::style($file, array('media' => $type)), "\n"; }?>	<? if (isset($styles['ie'])): ?>	  <!--[if IE]>		<?php foreach($styles['ie'] as $file => $type) { echo HTML::style($file, array('media' => $type)), "\n"; }?>  	  <![endif]-->  	<? endif; ?>  <?php foreach($scripts as $file) { echo HTML::script($file), "\n"; }?>  </head>  <body>	<div id="container">    	<div id="header">    		<a id="logo" class="text3d" href="<?php echo url::site() ?>">flow</a>    		<div class="header-stream-yellow"></div>    		<div class="header-stream-magenta"></div>    		<div class="header-stream-green"></div>    		<div class="header-stream-blue"></div>			<?php echo $header;?>			<ul id="menu">				<? if (!Auth::instance()->logged_in()): ?>					<li><? echo HTML::anchor('',__('Home')) ?></li>					<li><? echo HTML::anchor('register',__('Register')) ?></li>					<li><? echo HTML::anchor('login',__('Login')) ?></li>				<? else: ?>						<li><? echo HTML::anchor('index',__('Flows')) ?></li>					<li>						<? echo HTML::anchor('@'.$username,__('Profile')) ?>						<ul>							<li><? echo HTML::anchor('@'.$username.'/info',__('Info')) ?></li>							<li><? echo HTML::anchor('@'.$username.'/picture',__('Picture')) ?></li>							<li><? echo HTML::anchor('@'.$username.'/password',__('Password')) ?></li>						</ul>					</li>					<li><? echo HTML::anchor('logout',__('Logout')) ?></li>				<? endif; ?>			</ul>		</div>		<div id="site">		<? if (isset($message)): ?>		<div id="message">			<?= $message ?>		</div>		<? endif; ?>		<? if (isset($error)): ?>		<div id="error">			<?= $error ?>		</div>		<? endif; ?>		<div id="content">			<?php echo $content;?>		</div>		</div>		<div id="footer">			<?php echo $footer;?>		</div>    </div>  </body></html>