<?php echo form::open(NULL, array('id' => 'create_stream')) ?><h4><?php echo form::label('title',__('Title')) ?></h4><? if (isset($errors['title'])) echo error::form($errors['title']); ?><?php echo form::input('title',isset($post) ? $post['title'] : '') ?><h4><?php echo form::label('uri',__('URI')) ?></h4><? if (isset($errors['uri'])) echo error::form($errors['uri']); ?><?php echo form::input('uri',isset($post) ? $post['uri'] : '')?><h4><?php echo form::label('color',__('Color')) ?></h4><? if (isset($errors['color'])) echo error::form($errors['color']); ?><div id="colorpicker"></div><?php echo form::input('color',isset($post) ? $post['color'] : '') ?><h4><?php echo form::label('description',__('Public description')) ?></h4><? if (isset($errors['description'])) echo error::form($errors['description']); ?><?php echo form::textarea('description',isset($post) ? $post['description'] : '') ?><br/><?php echo form::button(NULL, __('Create'), array('type' => 'submit')) ?><?php echo form::close() ?><script type="text/javascript">$(function(){	$('input[name=title]').blur(function(){		$('input[name=uri]').attr('value',cleanURI($(this).attr('value')));	});		$('input[name=uri]').blur(function(){		uri = cleanURI($(this).attr('value'))		$(this).attr('value',uri);	});		$('input[name=color]').hide();		$('#colorpicker').colorPicker({			  		defaultColor: "<?php echo isset($post) ? $post['color'] : -1 ?>",  		click: function(color){$('input[name=color]').attr('value',color);}	});});</script>