<?php echo form::open(NULL, array('id' => 'add_tags')) ?><h1><?php echo __('Add tags') ?></h1><h4><?php echo form::label('tags',__('Tags')) ?></h4><? if (isset($errors['tags'])) echo $errors['tags']; ?><div id="tags">	<div class="tag">		<?php echo form::input('tags[1]','',array('id'=>'tag1')) ?>	</div>	<?php echo form::button('add_tag','Add tag',array('type'=>'button','id'=>'add_tag')) ?>	<?php echo form::button('remove_tag','Remove tag',array('type'=>'button','id'=>'remove_tag','disabled'=>'disabled')) ?></div><?php echo form::button(NULL, __('Save'), array('type' => 'submit')) ?><?php echo form::close() ?><!-- Following script modified from http://charlie.griefer.com/blog/index.cfm/2009/9/17/jQuery-Dynamically-Adding-Form-Elements --><script type="text/javascript">$(document).ready(function() {	$('#remove_tag').click(function(){		var num = new Number($('#tags div').length);		if (num > 1) {			$('#tag' + num).parent('div').remove();			if (num == 2) {				$('#remove_tag').attr('disabled','disabled');			}		} 	});	$('#add_tag').click(function() {		var num		= $('#tags div').length;	// how many "duplicatable" input fields we currently have		var new_num	= new Number(num + 1);		// the numeric ID of the new input field being added		// create the new element via clone(), and manipulate it's ID using newNum value		var elem = $('#tag' + num).parent('div').clone();		// manipulate the name/id values of the input inside the new element		elem.children(':first').attr('id', 'tag' + new_num).attr('name', 'tags[' + new_num + ']').attr('value','');			// insert the new element after the last "duplicatable" input field		$('#tag' + num).parent('div').after(elem);		// enable the "remove" button		$('#remove_tag').attr('disabled','');	});});</script>