<?php echo form::open(NULL, array('id' => 'edit_tag')) ?><h4><?php echo form::label('category',__('Category')) ?></h4><p><?php echo __('voluntary') ?></p><? if (isset($errors['category'])) echo error::form($errors['category']); ?><?php echo form::input('category',isset($post) ? $post['category'] : $tag->category)?><h4><?php echo form::label('title',__('Title')) ?></h4><? if (isset($errors['title'])) echo error::form($errors['title']); ?><?php echo form::input('title',isset($post) ? $post['title'] : $tag->title)?><h4><?php echo form::label('color',__('Color')) ?></h4><? if (isset($errors['color'])) echo error::form($errors['color']); ?><div id="colorpicker"></div><?php echo form::input('color',isset($post) ? $post['color'] : $tag->color) ?><br/><?php echo form::button(NULL, __('Save'), array('type' => 'submit')) ?><?php echo form::close() ?><script type="text/javascript">$(function(){	$('input[name=color]').hide();		$('#colorpicker').colorPicker({			  		defaultColor: "<?php echo isset($post) ? $post['color'] : $tag->color ?>",  		click: function(color){$('input[name=color]').attr('value',color);}	});});</script>