<h1><?php echo __('New tag') ?></h1><?php echo form::open(NULL, array('id' => 'new_tag')) ?><h4><?php echo form::label('title',__('Title')) ?></h4><? if (isset($errors['title'])) echo $errors['title']; ?><?php echo form::input('title',isset($post) ? $post['title'] : '')?><h4><?php echo form::label('color',__('Color')) ?></h4><? if (isset($errors['color'])) echo $errors['color']; ?><div id="colorpicker"></div><?php echo form::input('color',isset($post) ? $post['color'] : '') ?><br/><?php echo form::button(NULL, __('Save'), array('type' => 'submit')) ?><?php echo form::close() ?><script type="text/javascript">$(function(){	$('input[name=color]').hide();		$('#colorpicker').colorPicker({			  		defaultColor: "<?php echo isset($post) ? $post['color'] : '' ?>",  		click: function(color){$('input[name=color]').attr('value',color);}	});});</script>