<div class="flow-controls"><a class="control event-first flow-event-first" data-stream='{"id": "0"}'></a><a class="control event-prev flow-event-prev" data-stream='{"id": "0"}'></a><a class="control event-next flow-event-next" data-stream='{"id": "0"}'"></a><a class="control event-last flow-event-last" data-stream='{"id": "0"}'"></a></div><div id="timeline"><div class="timeline_info"><div class="clearfix">&nbsp;</div> <!-- height hack --><div class="clearfix">&nbsp;</div> <!-- height hack --><ul>	<?php foreach($flow['streams'] as $id => $stream): ?>		<li style="background-color: <?php echo $stream->color ?>">			<div>				<div class="stream-title">				<?php echo html::anchor($stream['uri'],$stream['title']) ?>				</div>				<div class="stream-meta">				<?php echo count($stream['events']) ?> <?php echo __('events in total') ?>				</div>				<div class="stream-actions">					<a class="control event-prev stream-event-prev" data-stream='{"id": "<?php echo $stream->id ?>"}'></a>					<a class="control event-next stream-event-next" data-stream='{"id": "<?php echo $stream->id ?>"}'"></a>					<br/>					<?php if ($access['master'] || isset($access['streams'][$id])): ?>						<?php echo html::anchor($stream['uri'].'/new',__('Add event'),array('class'=>'new-event')) ?>					<? endif; ?>				</div>			</div>		</li>	<?php endforeach; ?></ul><div class="new-stream clearfix" style="height: 20px;"><?php if ($access['master']): ?>	<?php echo html::anchor(Route::get('stream-new')->uri(array('flow'=>$flow->uri)),'<span class="add-one"></span>'.__('Add stream')) ?><?php endif; ?></div></div><div class="timeline_body"><!-- day --><ul class="dayline clearfix" style="width: <?php echo $width ?>px">	<?php $time = time::date2timestamp($flow->start_date); ?>	<?php while ($time < time::date2timestamp($flow->end_date) + 24*3600): ?>		<li class="day"><?php echo  strftime('%A %e. %B - %Y',$time) ?></li>		<?php $time+= 24*3600; ?>	<? endwhile; ?></ul><!-- hours --><ul class="hourline clearfix" style="width: <?php echo $width; ?>px">	<li class="halfhour" style="width: <?php echo ceil($scale/2); ?>px">&nbsp;</li>	<?php $time = 1; ?>	<?php while ($time*3600 < time::date2timestamp($flow['end_date']) + 24*3600 - time::date2timestamp($flow['start_date'])): ?>		<li class="hour" style="width: <?php echo $scale; ?>px"><?php echo $time%24 ?>:00</li>		<?php $time++; ?>	<? endwhile; ?></ul><!-- --><?php foreach ($flow['streams'] as $sid => $stream): ?><?php $c = color::hex2RGB($stream->color); ?><ul class="stream clearfix" data-stream='{"sid": "<?php echo $sid ?>"}' style="width: <?php echo $width ?>px; background-color: <?php echo $stream['alpha'] ?>"><?php foreach($stream['events'] $eid => $event): ?>	<?php $pos = $event['position']*$scale/2 ?>	<li class="event<?= $eid ?> drag" style="top: 0; left: <?php echo $pos ?>px; width: <?php echo $event['width']*$scale ?>px" data-event='{"position": "<?php echo $event['position'] ?>", "width" : "<?php echo $event['width'] ?>"}'>		<div class="event" style="background-color: <?php echo $stream->color ?>">			<a href="<?php echo $event['uri'] ?>"><?php echo $event['title'] ?></a>			<?php echo $event['body']; ?>			<!--<div class="draghandle"></div>			<div class="resizehandle"></div>-->		</div>	</li><?php endforeach; ?></ul><?php endforeach; ?><div class="clearfix" style="height: 20px; width: 1024px"></div> <!--- WTF??? --></div><div style="z-index: 1; position: relative; top: -21px; border-width: 0 1px; border-style: solid; border-color: red; height: 20px; margin-left: 150px; overflow: hidden" class="clearfix"><?php foreach($flow['range'] as $i=>$day): ?>	<div style="float: left; width: <?php echo number_format(round(100/count($flow['range']),2),2) ?>%">		<div style="height: 20px; border-width: 0 1px; border-style: solid; border-color: transparent #660000 transparent red; text-align: center"> <?php echo strftime('%A',$day) ?> </div>	</div><?php endforeach; ?></div><script type="text/javascript">$(function(){	events = new Object();events[0] = new Array();		streams = $('#streams-scrollable').jScrollPane(		{			showArrows: false,			horizontalGutter: 0,			animateScroll: true		}	);	api = streams.data('jsp');		$('.stream').each(function(){		id = $(this).data("stream").id; 		events[id] = new Array();		i=0;		$('li',this).each(function(){			events[id][i] = $(this).position().left;			events[0].push(events[id][i]);			i++;		});	});		$('a.event-prev').click(function(){		match = bestmatch(api.getContentPositionX(),events[$(this).data('stream').id]);		api.scrollToX(match[0]);	});		$('a.event-next').click(function(){		match = bestmatch(api.getContentPositionX(),events[$(this).data('stream').id]);		api.scrollToX(match[1]);	});		$('a.event-first').click(function(){		match = bestmatch(api.getContentPositionX(),events[$(this).data('stream').id]);		api.scrollToX(match[2]);	});		$('a.event-last').click(function(){		match = bestmatch(api.getContentPositionX(),events[$(this).data('stream').id]);		api.scrollToX(match[3]);	});});function bestmatch(goal,theList){	var smallest = null;	var biggest = null;	var closeless = null;	var closegreat = null;	$.each(theList, function(){		if (smallest == null || this < smallest) {			smallest = this;		}		if (biggest == null || this > biggest) {			biggest = this;		}	  	if (this < goal && (closeless==null || Math.abs(this - goal) < Math.abs(closeless - goal))) {    		closeless = this;		}		if (this > goal && (closegreat==null || Math.abs(this - goal) < Math.abs(closegreat - goal))) {			closegreat = this;		}	});		if (closegreat == null) {		closegreat = biggest;	}	if (closeless == null) {		closeless = smallest;	}		return Array(closeless,closegreat,smallest,biggest);}function rescale(scale){	width = <?php echo $flow['width'] ?> scale;		$('.dayline').css({'width' : width });	$('.hourline').css({'width' : width });	$('.dayline').css({'width' : width });		$('.event').each(function(){		position = $(this).data('position');		width = $(this).data('width');		$(this).css({'left' : position*scale, 'width' : width*scale}); 	});}</script><style><?php foreach($tags as $tag): ?>.tag<?php echo $tag->id ?> {	background-color: <?php echo $tag->color ?>}<?php endforeach; ?></style><script type="text/javascript">$(function(){ 	$('a.tooltip').tipsy({live: true});});/*jQuery(function($){   $('.drag')      .click(function(){         $( this ).toggleClass("selected");      })      .drag("init",function(){         if ( $( this ).is('.selected') )            return $('.selected');                       })      .drag("start",function( ev, dd ){         dd.attr = $( ev.target ).attr("className");         dd.width = $( this ).width();      })     .drag("end",function( ev, dd ){      	$( this ).data('id');      	$( this ).width();		$( this ).position.left;      })      .drag(function( ev, dd ){         var props = {};         if ( dd.attr.indexOf("resize") > -1 ){            props.width = Math.max( 144, Math.round((dd.width + dd.deltaX)/72)*72 );         }         if ( dd.attr.indexOf("draghandle") > -1 ){            props.top = 0;            props.left = Math.round((api.getContentPositionX() + dd.offsetX -170)/72)*72;         }         $( this ).css( props );      });});*/</script>