<?php echo Fust::markdown($user->description) ?><?php echo $user->name ?> is <b><?php echo $flow_user->master ? __('a flow master') : __('an ordinary flow user') ?></b> of <?php echo $flow->title ?>.<?php if ($access['master'] == 1): ?>	<?php if ($flow_user->master): ?>		<?php echo html::anchor($flow->uri.'/@'.$user->username.'/degrade',__('Degrade to flow user'),array('class'=>'action')) ?>	<?php else: ?>		<?php echo html::anchor($flow->uri.'/@'.$user->username.'/promote',__('Promote to flow master'),array('class'=>'action')) ?>	<?php endif; ?><? endif; ?><h3>Stream editor</h3><p><?php if(count($edits)>0): ?><?php echo $user->name.' '.__('is appointed  editor of the following streams') ?>: </p><table class="vspace"><?php foreach ($edits as $edit): ?>	<tr>		<td><?php echo $streams[$edit]->title ?></td>		<?php if($access['master']): ?>		<td><?php echo html::anchor($flow->uri.'/'.$streams[$edit]->uri.'/withdraw/@'.$user->username,__('Withdraw editorship'),array('class'=>'action')) ?></td>		<? endif; ?>	</tr><? endforeach; ?></table><p><?else: ?><?php echo $user->name.' '.__('has not been appointed editor of any streams') ?>.<? endif; ?><?php if (count($edits)!=count($streams)): ?><?php echo __('Appoint as editor for the following streams:') ?></p><?php $stream_editors = count($edits)>0 ? array_combine($edits,$edits) : array() ?><table class="vspace"><?php foreach(array_diff_key($streams,$stream_editors) as $stream): ?>	<tr>		<td><?php echo $stream->title ?></td>		<?php if($access['master']): ?>		<td><?php echo html::anchor($flow->uri.'/'.$stream->uri.'/appoint/@'.$user->username,__('Appoint editor'),array('class'=>'action')) ?></td>		<? endif; ?>	</tr><?php endforeach; ?></table><?php endif; ?>